/* eslint-disable prettier/prettier */
/* eslint-disable no-unused-expressions */
/* eslint-disable no-console */
import * as React from 'react';
import * as $ from 'jquery';
import defaultMovieImg from '../assets/img/movieIcon-150x150.png';
import axios from 'axios';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';
import PropTypes from 'prop-types';
import { Highlight } from 'react-instantsearch-dom';
const { useState, useEffect } = React;

const Hit = props => {
  const [movieImgSrc, setMovieImgSrc] = useState(props.hit.image);
  const [hover, setHover] = useState(false);
  const [fullMovieData, setFullMovieData] = useState(false);
  const [randomIndex, setRandomIndex] = useState(null);
  const [validateActorImgs, setValidateActorImgs] = useState(false);
  const [actors, setActors] = useState(null);
  const [open, setOpen] = useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);
  const movieImg = new Image();
  movieImg.src = props.hit.image;

  useEffect(() => {
    setRandomIndex((Math.random() * 10).toFixed(2))
    if (!movieImg.width || !movieImg.height) {
      axios
        .get(
          `https://api.themoviedb.org/3/search/movie?api_key=15d2ea6d0dc1d476efbca3eba2b9bbfb&query=${props.hit.title}&year=${props.hit.year}`
        )
        .then(data => {
          !data.data.results.length
            ? setMovieImgSrc(defaultMovieImg)
            : setMovieImgSrc(
                `http://image.tmdb.org/t/p/w500${data.data.results[0].poster_path}`
              );
        });
    }
  }, []);

  const onHover = () => {
    setHover(true);
  };

  const onLeave = () => {
    setHover(false);
  };

  const toggleMovie = async () => {
    if(!fullMovieData) {
      await setFullMovieData(true);
      console.log($(`.ais-Hits-item:has(div.${randomIndex})`),`.ais-Hits-item:has(div.${randomIndex})` )
      $(`.ais-Hits-item:has(div.movieData)`).addClass('fullMovie');
      if(!validateActorImgs) {
          const actorFacets = props.hit.actor_facets;
          const actorsArr = [];
          for(let i = 0; i < 4; i++) {
            if(actorFacets[i]) {
              console.log('flag', actorFacets[i].split('|')[1], actorFacets[i])
              axios.get(`https://api.themoviedb.org/3/search/person?api_key=15d2ea6d0dc1d476efbca3eba2b9bbfb&query=${actorFacets[i].split('|')[1]}`).then(data => {
                  const actorImg = data.data.results.length ? `http://image.tmdb.org/t/p/w500${data.data.results[0].profile_path}` : actorFacets[i].split('|')[1];
                  actorsArr.push(
                    <div className="col-6" key={actorFacets[i].split('|')[1]}>
                      <img src={actorImg} alt={actorFacets[i].split('|')[1]} className="movieImg" />
                      <h3>{actorFacets[i].split('|')[1]}</h3>
                    </div>
                  )
                })
            }
          }
          setActors(actorsArr);
          setValidateActorImgs(true);
      }
    } else {
      setFullMovieData(false);
      $(`.ais-Hits-item:has(div.movieData)`).removeClass('fullMovie');
    }
  }



  return (
    <div className={`row ${fullMovieData ? "movieData" : ""}`} onMouseEnter={onHover} onMouseLeave={onLeave} onClick={() => { toggleMovie(); handleOpen();}}>
      <div className={fullMovieData ? 'col-6 col-md-3' : 'col-12'}>
        <img src={movieImgSrc} className="movieImg" alt="" />
        <div className="hit-name pt-2">
          <Highlight attribute="title" hit={props.hit} />
        </div>
        <div className="hit-description whiteThemeText hit-year">
          {props.hit.year}
        </div>
      </div>
      {/* {fullMovieData ? 
        <>
        <div className='col-6 col-md-3'>
            <div className='genres'>{props.hit.genre.map(genre => <span style={{ backgroundColor: props.hit.color }} className="badge m-1">{genre}</span>)}</div>
          <div>
            <p className="score">Audience Score: {`${props.hit.score.toFixed(1).replace('.', '')}%`}</p>            
            <span>
              {Array(Math.floor(props.hit.rating)).fill(<i className="fa fa-star"></i>)}
              {(props.hit.rating) - Math.floor(props.hit.rating) === 0 ? ('') : (<i className="fa fa-star-half"></i>)}
            </span>
          </div>
        </div>
        <div className='col-6'>
          <div className='row'>{actors}</div>
        </div>
        <svg xmlns="http://www.w3.org/2000/svg" width="32" height="32" viewBox="0 0 24 24" fill="none" stroke="#66FCF1" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="col-2 col-md-1 feather feather-x"><line x1="18" y1="6" x2="6" y2="18" /><line x1="6" y1="6" x2="18" y2="18" /></svg></>
      : <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="#66FCF1" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={`col-3 feather feather-arrow-right d-${hover ? 'flex' : 'none'}`}><line x1="5" y1="12" x2="19" y2="12"></line><polyline points="12 5 19 12 12 19"></polyline></svg>} */}

      <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={`col-3 feather feather-info d-${hover ? 'flex' : 'none'}`}><circle cx="12" cy="12" r="10"/><line x1="12" y1="16" x2="12" y2="12"/><line x1="12" y1="8" x2="12.01" y2="8"/></svg>
    </div>
  );
};

Hit.propTypes = {
  hit: PropTypes.object.isRequired,
};

export default Hit;
