/* eslint-disable react/jsx-key */
/* eslint-disable prettier/prettier */
/* eslint-disable no-unused-expressions */
/* eslint-disable no-console */
import * as React from 'react';
import defaultMovieImg from '../assets/img/movieIcon-150x150.png';
import axios from 'axios';
import Box from '@mui/material/Box';
import Modal from '@mui/material/Modal';
import PropTypes from 'prop-types';
const { useState, useEffect } = React;

const Hit = props => {
  const [movieImgSrc, setMovieImgSrc] = useState(props.hit.image);
  const [validateActorImgs, setValidateActorImgs] = useState(false);
  const [actors, setActors] = useState(null);
  const [open, setOpen] = useState(false);
  const toggleModal = () => open ? setOpen(false) : setOpen(true);

  const validate = async () => {
    const movieImg = new Image();
    movieImg.src = props.hit.image;
    if (!movieImg.width || !movieImg.height) {
      const results = await axios.get(`https://api.themoviedb.org/3/search/movie?api_key=15d2ea6d0dc1d476efbca3eba2b9bbfb&query=${props.hit.title}`).data;
      console.log('flag', results)
      // results.length && results[0].poster_path ? setMovieImgSrc(`http://image.tmdb.org/t/p/w500${results[0].poster_path}`) : setMovieImgSrc(defaultMovieImg);
    }
    if(!validateActorImgs) {
        const actorFacets = props.hit.actor_facets;
        const actorsArr = [];
        for(let i = 0; i < 4; i++) {
          if(actorFacets[i]) {
            await axios.get(`https://api.themoviedb.org/3/search/person?api_key=15d2ea6d0dc1d476efbca3eba2b9bbfb&query=${actorFacets[i].split('|')[1]}`).then(data => {
                const actorImg = (data.data.results.length && data.data.results[0].profile_path) ? `http://image.tmdb.org/t/p/w500${data.data.results[0].profile_path}` : actorFacets[i].split('|')[1];
                actorsArr.push(
                  <div className="col-6 col-md-3 text-center" key={actorFacets[i].split('|')[1]}>
                    <img src={actorImg} alt={actorFacets[i].split('|')[1]} className="modalMovieImg" />
                    <p className="actor-name">{actorFacets[i].split('|')[1]}</p>
                  </div>
                )
              })
          }
        }
        setActors(actorsArr);
        setValidateActorImgs(true);
    }
  }
  useEffect(() => {
    // image urls in the movie dataset provided in the github url were out of date/no good
    // replaced any image that wasnt resolving anymore with a fresh image from the movieDB api
    validate();
  }, []);

  return (
    <div className={`row movie-item`} onClick={toggleModal}>
      <div className={'col-12'}>
        <img src={movieImgSrc} onError={defaultMovieImg} className="movieImg" alt="" />
        <div className="hit-name pt-2">
          <span>{props.hit.title}</span>
        </div>
        <div className="hit-description whiteThemeText hit-year">
          {props.hit.year}
        </div>
      </div>
      <Modal open={open} onClose={toggleModal} aria-labelledby="modal-modal-title" aria-describedby="modal-modal-description">
        <Box className="movieModal rounded">
          <div className="row">
            <div className='col-12 text-center'>
                <div className="hit-name pt-2">
                  <span>{props.hit.title}</span>
                </div>
                <div className='genres'>{props.hit.genre.map(genre => <span style={{ backgroundColor: props.hit.color }} className="badge m-1">{genre}</span>)}</div>
              <div>
                <p className="score">Audience Score: {`${props.hit.score.toFixed(1).replace('.', '')}%`}</p>            
                <span>
                  {Array(Math.floor(props.hit.rating)).fill(<i className="fa fa-star"></i>)}
                  {(props.hit.rating) - Math.floor(props.hit.rating) === 0 ? ('') : (<i className="fa fa-star-half"></i>)}
                </span>
              </div>
            </div>
            <div className='col-12 mt-2'>
              <div className='row d-flex justify-content-center'>{actors}</div>
            </div>
          </div>
        </Box>
      </Modal>
      <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={`col-3 feather feather-info d-none`}><circle cx="12" cy="12" r="10"/><line x1="12" y1="16" x2="12" y2="12"/><line x1="12" y1="8" x2="12.01" y2="8"/></svg>
    </div>
  );
};

Hit.propTypes = {
  hit: PropTypes.object.isRequired,
};

export default Hit;
